import React from 'react';
import clsx from 'clsx';
import {GeneralMap} from './Map2';
import {GraphicGroup} from './Graphic2';
import {positions} from '@material-ui/system';
import {makeStyles} from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import InputLabel from '@material-ui/core/InputLabel';
import Typography from '@material-ui/core/Typography';
import MenuItem from '@material-ui/core/MenuItem';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Autocomplete from '@material-ui/lab/Autocomplete';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import Select from '@material-ui/core/Select';
import ButtonBase from '@material-ui/core/ButtonBase';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';
import {mainListItems, secondaryListItems} from './SidebarList';
import FilterGroup from './FilterGroup';
import {createMuiTheme, ThemeProvider} from '@material-ui/core/styles';
import PFDinRegularWoff from "../fonts/PFDinTextCondPro-Regular.woff";
import '../style.css';
import street_list from '../data/street_list.json';
import ts_list from '../data/ts_balance_list.json';

const drawerWidth = 100;

const pfdinRegular = {
  fontFamily: 'PFDinTextCondPro-Regular',
  fontStyle: 'normal',
  fontDisplay: 'swap',
  fontWeight: '400',
  src: `
    local('PFDinTextCondPro-Regular'),
    url(${PFDinRegularWoff}) format('woff')
    `
}

const theme = createMuiTheme({
  typography: {
    fontFamily: 'PFDinTextCondPro-Regular !important',
    fontSize: 11,
    textTransform: 'uppercase'
  },
  typographyStyle: {
    color: "red"
  },
  overrides: {
    MuiCssBaseline: {
      '@global': {
        '@font-face': [pfdinRegular]
      }
    }
  }
});

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex'
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create([
      'width', 'margin'
    ], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    })
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create([
      'width', 'margin'
    ], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  menuButton: {
    marginRight: 36
  },
  menuButtonHidden: {
    display: 'none'
  },
  menuListItems: {
    position: "absolute",
    bottom: 0,
    alignSelf: 'center'
  },
  title: {
    flexGrow: 1
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen
    })
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9)
    }
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
    background: '#E5E5E5',
    marginLeft: '68px',
    paddingTop: '40px'
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4)
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column'
  },
  fixedHeight: {
    height: 500
  },
  container: {
    background: "#E5E5E5",
    paddingLeft: 40,
    paddingRight: 40
  },
  drawerPaper: {
    background: "#252F4A",
    width: "68px"
  },
  listText: {
    color: "red"
  }
}));

const filter1 = {
  label: 'Балансовая группа',
  menu_items: [
    {
      key: '1',
      value: '1'
    }, {
      key: '2',
      value: '2'
    }, {
      key: '3',
      value: '3'
    }
  ]
}

const filter2 = {
  label: 'Тип балансовой группы',
  menu_items: [
    {
      key: '1',
      value: '11'
    }, {
      key: '2',
      value: '22'
    }, {
      key: '3',
      value: '33'
    }
  ]
}

const address_search = {
  label: '',
  help_text: 'Найти адрес',
  menu_items: street_list.street_list
}

const ts_select = {
  label: 'Трансформаторная подстанция',
  help_text: 'Выберете из списка',
  menu_items: ts_list.ts_list
}

class ProductTable extends React.Component {

  render() {
    const filterText = this.props.filterText;

    // console.log(this.props);
    return (<table>
      <thead>
        <tr>
          <th>Name</th>
          <th>Price</th>
        </tr>
      </thead>
      <tbody>{}</tbody>
    </table>);
  }

}

class FilterOptions extends React.Component {
  constructor(props) {
    super(props);
    this.handleFilterTextChange = this.handleFilterTextChange.bind(this);
  }

  state = {
    value: "empty"
  }

  handleFilterTextChange(e, value) {
    if (value.props !== null) {
      this.props.onFilterTextChange(value.props.balance_group_list);
    }
    this.setState({value: e.target.value});
  }

  render() {
    return (<div>
      <InputLabel id="demo-simple-select-placeholder-label-label">
        {ts_select.label}
      </InputLabel>
      <Select value={this.state.value} onChange={this.handleFilterTextChange}>
        <MenuItem value="empty">{ts_select.help_text}</MenuItem>
        {ts_select.menu_items.map((item, index) => <MenuItem  balance_group_list={item.balance_group_list} key={item.id} value={item.id}>{item.name}</MenuItem>)}
      </Select>
    </div>);
  }

}

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.handleFilterTextChange = this.handleFilterTextChange.bind(this);
  }

  handleFilterTextChange(e, value) {
    if (value !== null) {
      this.props.onFilterTextChange(value.balance_index);
    }else{
      this.props.onFilterTextChange('');
    }
  }

  render() {
    return (<div>
      <FormControl>
        <Autocomplete id="street_search"  options={address_search.menu_items} getOptionLabel={(option) => option.name} style={{
            width: 300
          }} onChange={this.handleFilterTextChange} renderInput={(params) => <TextField {...params} label={address_search.help_text} margin="normal"/>}/>
      </FormControl>

    </div>);
  }

}

export default class BalanceGroup2 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      filterText: ''
    };

    this.handleFilterTextChange = this.handleFilterTextChange.bind(this);
  }

  handleFilterTextChange(filterText) {
    this.setState({filterText: filterText});
  }


  render() {
    return (<div key='div1'>
      <SearchBar filterText={this.state.filterText} onFilterTextChange={this.handleFilterTextChange}/>
      <FilterOptions filterText={this.state.filterText} onFilterTextChange={this.handleFilterTextChange}/>
      {/* <ProductTable
        filterText={this.state.filterText}
      /> */}
      {/* <GeneralMap filterText={this.state.filterText}/> */}
      <GraphicGroup filterText={this.state.filterText} key={1}/>
    </div>);
  }
}
